declare const typeDefs = "\n  \"A user account in the system\"\n  type User {\n    _id: ID!\n    firstName: String!\n    lastName: String!\n    email: String!\n    sounds: [Sound!]!\n    createdAt: String!\n    updatedAt: String!\n  }\n\n  \"A sound recording with its associated metadata\"\n  type Sound {\n    _id: ID!\n    userId: ID!\n    title: String!\n    audioData: String!  # Base64 encoded audio data\n    instrument: Instrument!\n    duration: Float\n    waveformData: [Float]\n    frequencyData: [Float]\n    createdAt: String!\n    updatedAt: String!\n  }\n\n  \"Instrument configuration for a sound recording\"\n  type Instrument {\n    piano: Boolean!\n    guitar: Boolean!\n    trumpet: Boolean!\n  }\n\n  \"Input for creating a new user\"\n  input UserInput {\n    firstName: String!\n    lastName: String!\n    email: String!\n    password: String!\n  }\n\n  \"Input for sound instrument configuration\"\n  input InstrumentInput {\n    piano: Boolean!\n    guitar: Boolean!\n    trumpet: Boolean!\n  }\n\n  \"Authentication response containing token and user data\"\n  type Auth {\n    token: String!\n    user: User!\n  }\n\n  type Query {\n    \"Get all users (admin only)\"\n    users: [User!]!\n\n    \"Get a user by email\"\n    user(email: String!): User\n\n    \"Get all sounds for the logged-in user\"\n    getSounds: [Sound!]!\n\n    \"Get a specific sound by ID\"\n    getSound(_id: ID!): Sound\n\n    \"Get the currently logged-in user's profile\"\n    me: User\n  }\n\n  type Mutation {\n    \"Create a new user account\"\n    addUser(input: UserInput!): Auth\n\n    \"Log in to an existing account\"\n    login(email: String!, password: String!): Auth\n\n    \"Create a new sound recording\"\n    createSound(\n      title: String!\n      audioData: String!\n      instrument: InstrumentInput!\n      duration: Float\n      waveformData: [Float]\n      frequencyData: [Float]\n    ): Sound!\n\n    \"Delete a sound recording\"\n    deleteSound(_id: ID!): Boolean!\n\n    \"Update user profile information\"\n    updateUserProfile(\n      firstName: String\n      lastName: String\n    ): User!\n  }\n";
export default typeDefs;
